for j in d:
    d[j]=round(d[j]*100/len(texte),2)

def clemax(d):
     v=list(d.values())
     k=list(d.keys())
     return k[v.index(max(v))]

def cle(frequence, d):
    cle=ord(clemax(d))-ord(clemax(frequence))
    if cle<0:
        cle+=26
    return cle

def decodage(message, cle):
    d = {chr(i+65):chr((i-cle) %26+65) for i in range(26)}
    messageDecode = ""
    for i in range(len(message)):
        messageDecode = messageDecode + d[message[i]]
    return messageDecode

print(decodage(texte, cle(frequence,d)))
print(cle(frequence, d))